document.addEventListener("DOMContentLoaded",()=>{const btnIniciarSocial=document.getElementById("iniciar-social");const juegoContainer=document.getElementById("juego-social");const seccionJuegoCard=document.querySelector("#seccion-social .seccion-juego-card");const nivelSocial=document.getElementById("nivel-social");const preguntaSocial=document.getElementById("pregunta-social");const btnResetSocial=document.getElementById("reset-social");const btnNextSocial=document.getElementById("next-social");const socialOptions=document.getElementById("social-options");const referenceImage=document.getElementById("reference-image");let nivelActual=1;let respuestasSeleccionadas=[];let juegoTerminado=!1;let juegoActivo=!1;const niveles={1:{pregunta:"Uno de mis compañeros(as) está discutiendo ¿Qué puedo hacer?",imagen:"level1-img-reference.webp",opciones:[{texto:"Decirle a la maestra(o).",imagen:"level1-img-alternative1.webp",puntos:{empatia:15,pensamiento:15,resolucion:15},},{texto:"Pelear con ellos(as).",imagen:"level1-img-alternative2.webp",puntos:{empatia:5,pensamiento:5,resolucion:5},},{texto:"Ignorar lo que sucede.",imagen:"level1-img-alternative3.webp",puntos:{empatia:10,pensamiento:10,resolucion:10},},],},2:{pregunta:"Tu amigo(a) quiere romper tu juguete ¿Qué haces?",imagen:"level2-img-reference.webp",opciones:[{texto:"Pegarle.",imagen:"level2-img-alternative1.webp",puntos:{empatia:5,pensamiento:5,resolucion:5},},{texto:"Lloras.",imagen:"level2-img-alternative2.webp",puntos:{empatia:10,pensamiento:10,resolucion:10},},{texto:"Decirle a tu mamá.",imagen:"level2-img-alternative3.webp",puntos:{empatia:15,pensamiento:15,resolucion:15},},],},3:{pregunta:"Si una persona extraña te ofrece algo ¿Qué harás?",imagen:"level3-img-reference.webp",opciones:[{texto:"Aceptar lo que me está dando.",imagen:"level3-img-alternative1.webp",puntos:{empatia:5,pensamiento:5,resolucion:5},},{texto:"Avisar a mis padres.",imagen:"level3-img-alternative2.webp",puntos:{empatia:15,pensamiento:15,resolucion:15},},{texto:"Salir corriendo.",imagen:"level3-img-alternative3.webp",puntos:{empatia:10,pensamiento:10,resolucion:10},},],},4:{pregunta:"Me cambio de ropa, y no me gusta lo que escogió mi mamá ¿Qué hago?",imagen:"level4-img-reference.webp",opciones:[{texto:"Me enojo o lloro.",imagen:"level4-img-alternative1.webp",puntos:{empatia:5,pensamiento:5,resolucion:5},},{texto:"Me cambio de ropa yo mismo.",imagen:"level4-img-alternative2.webp",puntos:{empatia:10,pensamiento:10,resolucion:10},},{texto:"Le explico porque no me gusta, y buscamos otra.",imagen:"level4-img-alternative3.webp",puntos:{empatia:15,pensamiento:15,resolucion:15},},],},5:{pregunta:"Si te piden que escojas el desayuno",imagen:"level5-img-reference.webp",opciones:[{texto:"Hot cakes.",imagen:"level5-img-alternative1.webp",puntos:{empatia:5,pensamiento:5,resolucion:5},},{texto:"Frutas con yogurt.",imagen:"level5-img-alternative2.webp",puntos:{empatia:15,pensamiento:15,resolucion:15},},{texto:"Huevos fritos.",imagen:"level5-img-alternative3.webp",puntos:{empatia:10,pensamiento:10,resolucion:10},},],},};if(btnIniciarSocial){btnIniciarSocial.addEventListener("click",()=>{if(window.juegosCompletados&&window.juegosCompletados.includes("social")){return}
seccionJuegoCard.style.display="none";juegoContainer.style.display="block";juegoActivo=!0;iniciarNivel(1)})}
if(btnResetSocial){btnResetSocial.addEventListener("click",()=>{const hayResultadosVisibles=juegoContainer.querySelector(".social-resultado");if(hayResultadosVisibles){return}
nivelActual=1;respuestasSeleccionadas=[];juegoTerminado=!1;iniciarNivel(1)})}
if(btnNextSocial){btnNextSocial.addEventListener("click",()=>{if(nivelActual<5){nivelActual++;iniciarNivel(nivelActual)}else{mostrarResultadoFinal()}})}
function iniciarNivel(nivel){nivelSocial.textContent=nivel;btnNextSocial.disabled=!0;juegoTerminado=!1;const nivelData=niveles[nivel];preguntaSocial.textContent=nivelData.pregunta;referenceImage.src=`img/misc/${nivelData.imagen}`;generarOpciones(nivel)}
function generarOpciones(nivel){socialOptions.innerHTML="";const nivelData=niveles[nivel];nivelData.opciones.forEach((opcion,index)=>{const opcionElement=document.createElement("div");opcionElement.classList.add("social-option");opcionElement.dataset.index=index;opcionElement.innerHTML=`
        <img src="img/misc/${opcion.imagen}" alt="Opción ${index + 1}">
        <div class="social-option-text">${opcion.texto}</div>
      `;opcionElement.addEventListener("click",()=>manejarSeleccion(opcionElement,index,nivel));socialOptions.appendChild(opcionElement)})}
function manejarSeleccion(elemento,index,nivel){if(juegoTerminado)return;juegoTerminado=!0;btnNextSocial.disabled=!1;const opcionesActivas=socialOptions.querySelectorAll(".social-option-selected");opcionesActivas.forEach((opcion)=>opcion.classList.remove("social-option-selected"));elemento.classList.add("social-option-selected");respuestasSeleccionadas.push({nivel:nivel,opcionSeleccionada:index,puntos:niveles[nivel].opciones[index].puntos,})}
function mostrarResultadoFinal(){socialOptions.innerHTML="";preguntaSocial.style.display="none";referenceImage.style.display="none";const puntajes=calcularPuntajes(respuestasSeleccionadas);window.puntajesJuegos.social={empatia:puntajes.empatia,pensamiento:puntajes.pensamiento,resolucion:puntajes.resolucion,};const resultadoElement=juegoContainer.querySelector(".social-resultado");const valoresAptitud=resultadoElement.querySelectorAll(".aptitud-valor");valoresAptitud[0].textContent=`${puntajes.empatia} pts`;valoresAptitud[1].textContent=`${puntajes.pensamiento} pts`;valoresAptitud[2].textContent=`${puntajes.resolucion} pts`;if(window.juegosCompletados&&!window.juegosCompletados.includes("social")){window.juegosCompletados.push("social")}
if(btnResetSocial){btnResetSocial.disabled=!0}
if(btnNextSocial){btnNextSocial.disabled=!0}
resultadoElement.style.display="block";const btnVolver=resultadoElement.querySelector(".btn-volver");btnVolver.addEventListener("click",()=>{limpiarJuego();juegoContainer.style.display="none";juegoActivo=!1;if(window.juegosCompletados&&!window.juegosCompletados.includes("social")){window.juegosCompletados.push("social")}
const btnSocial=document.getElementById("iniciar-social");if(btnSocial){btnSocial.textContent="FINALIZADO";btnSocial.disabled=!0;btnSocial.classList.add("juego-finalizado")}
preguntaSocial.style.display="block";referenceImage.style.display="block";seccionJuegoCard.style.display="flex";window.mostrarSoloSeccionInicio("inicio")})}
function calcularPuntajes(respuestas){let puntajeEmpatia=0;let puntajePensamiento=0;let puntajeResolucion=0;respuestas.forEach((respuesta)=>{if(respuesta.puntos.empatia===15||respuesta.puntos.empatia===10){puntajeEmpatia+=respuesta.puntos.empatia}
if(respuesta.puntos.pensamiento===15||respuesta.puntos.pensamiento===10){puntajePensamiento+=respuesta.puntos.pensamiento}
if(respuesta.puntos.resolucion===15||respuesta.puntos.resolucion===10){puntajeResolucion+=respuesta.puntos.resolucion}});return{empatia:puntajeEmpatia,pensamiento:puntajePensamiento,resolucion:puntajeResolucion,}}
function limpiarJuego(){const resultadoElement=juegoContainer.querySelector(".social-resultado");if(resultadoElement){resultadoElement.style.display="none"}
nivelActual=1;respuestasSeleccionadas=[];juegoTerminado=!1}
window.jugarSocial={limpiarJuego,iniciarNivel,}})